import React, { useState,useEffect } from 'react'
import ReactDOM from 'react-dom'

function App() {
  const [count, setCount] = useState(0)
  document.title = 
  //参数是一个函数，这个组件会在组件渲染好的时候执行（componentDidMount+componentDid）
  useEffect(()=>{})
  return (
    <div>
      <h1>根组件</h1>
      <div>点击次数:{count}</div>
      <button
        onClick={() => {
          setCount(count + 1)
        }}
      >
        +1
      </button>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
