import React from 'react'
import ReactDOM from 'react-dom'
import Mouse from './Mouse'
const element = (
  <div>
    <h1>根组件</h1>
    <Mouse
      render={
        (params) => {
          return (<div>
            <h3>当前鼠标的位置</h3>
            <div>({this.state.x},{this.state.y})</div>
        </div>)
        }
      }
    ></Mouse>
  </div>
)

ReactDOM.render(element, document.getElementById('root'))
