import { useEffect, useState } from 'react'
import ReactDOM from 'react-dom'
import img from './dog.jpg'
function Dog() {
  const [position, setPosition] = useState({
    x: 0,
    y: 0,
  })
  useEffect(()=>{
    const move = (/** @type {{ pageX: any; pageY: any; }} */ e)=>{
      setPosition({
        x:e.pageX,
        y:e.pageY
      })
    }
    document.addEventListener('mousemove',move)
    return ()=>{
      document.removeEventListener('mousemove',move)
    }
  },[])
  return (
    <img
      src={img}
      style={{ position: 'absolute', left:position.x, top: position.y, width: 100, height: 100 }}
      alt=""
    />
  )
}
ReactDOM.render(<Dog></Dog>, document.getElementById('root'))
