//提供todos相关的action
import axios from 'axios'
//获取任务列表
export const getListAsync = () => {
  return async (dispatch) => {
    //发送请求
    const res = await axios.get('http://localhost:8888/todos')
    // console.log(res);
    dispatch({
      type:'GET_LIST',
      list:res.data 
    })
  }
}
