//挣钱
export const addMoney = (money)=>{
    return {
        type:'ADD_MONEY',
        money,
    }
}

export const buy = (money)=>{
    return {
        type:'BUY',
        money,
    }
}

//如果是同步action，直接返回一个action对象
//如果是异步action，需要返回一个异步函数，在异步函数zhong
