import { useDispatch } from "react-redux"
import { changeDone, delTodo } from "../store/actions/todos"

/**
 * 1.给删除注册点击事件。。。点击事件需要传递参数id
 * 2.再事件处理程序中获取id 并且dispatch delTodo中的action
 * 3.在action/todo.js文件中定义delTodo的action
 *   带异步的，所以需要发送请求  并且dispatch（{}）
 * 4.在reducer中处理这个action  根据id过滤对应的任务
 */

export default function TodoItem({item}) {
  const dispatch = useDispatch()
  const del = (id)=>{
    dispatch(delTodo(id))
  }
  const change = (id,done)=>{dispatch(changeDone(id,done))}
  return (
    <li className={item.done?'completed':''}>
      <div className="view">
        <input className="toggle" type="checkbox" checked={item.done} onChange={(e)=>change(item.id,e.target.checked)}/>
        <label onDoubleClick={()=>showEdit()}>{item.name}</label>
        <button className="destroy" onClick={()=>del(item.id)}/>
      </div>
      <input className="edit" defaultValue="Create a TodoMVC template" />
    </li>
  )
}
