//提供todos相关的action
import axios from 'axios'

//获取任务列表  异步
export const getList = () => {
  return async (dispatch) => {
    //发送请求
    const res = await axios.get('http://localhost:8888/todos')
    dispatch({
      type:'GET_LIST ',
      list:res.data
    })
  }
}
